import Router from "next/dist/next-server/lib/router/router";
import React, { Component } from "react";

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mail: "",
      password: "",
    };
  }

  componentDidMount() {
    document.getElementById("loginmailinput").focus();
    document.addEventListener("keyup", this.handleKeyUp);
  }

  login(e) {
    if (e) {
      e.preventDefault();
    }
    console.log(this);
    const body = {
      mail: this.state.mail,
      password: this.state.password,
    };
    this.props.store.login(body, this.props.loggedIn);
  }

  handleChange(e) {
    e.preventDefault();
    if (e.target.id === "loginmailinput") {
      this.setState({ mail: e.target.value });
    } else if (e.target.id === "loginpasswordinput") {
      this.setState({ password: e.target.value });
    }
  }

  switchToRegister(e) {
    if (e) {
      e.preventDefault();
    }
    document.getElementById("loginmailinput").value = "";
    document.getElementById("loginpasswordinput").value = "";
    Router.push('/register');
  }

  handleKeyUp(e) {
    e.preventDefault();
    if (e.key === "Enter") {
      this.login();
    }
  }

  render() {
    return (
      <div className={this.state.showHideClassName}>
        <div className="body-4">
          <div class="logindiv">
            <div class="loginclosediv" onClick={this.hide.bind(this)}>
              <a href="#" class="closeblock w-inline-block">
                <img
                  src="images/cancel.png"
                  loading="lazy"
                  alt=""
                  class="image-4"
                />
              </a>
            </div>
            <div class="loginheader">Anmelden</div>
            <div class="loginform w-form">
              <form
                id="email-form"
                name="email-form"
                data-name="Email Form"
                class="form-3"
              >
                <input
                  onChange={this.handleChange.bind(this)}
                  type="text"
                  class="logintextfield mail w-input"
                  maxlength="256"
                  name="field"
                  data-name="Field"
                  placeholder="Mail"
                  id="loginmailinput"
                  required=""
                />
                <input
                  onChange={this.handleChange.bind(this)}
                  onKeyUp={this.handleKeyUp.bind(this)}
                  type="password"
                  class="logintextfield password w-input"
                  maxlength="256"
                  name="field-2"
                  data-name="Field 2"
                  placeholder="Passwort"
                  id="loginpasswordinput"
                  required=""
                />
                <a
                  onClick={this.login.bind(this)}
                  data-w-id="2e7716f4-fa06-b98c-67d2-94a074914c30"
                  href="#"
                  class="button login w-button"
                >
                  ANMELDEN
                </a>
                <div class="loginbottomtext forgotpw">
                  <a
                    href="#"
                    onClick={this.props.showForgotPassword}
                    className="link-5"
                  >
                    Password vergessen?
                  </a>
                </div>
              </form>
            </div>
            <div class="loginseparatordiv">
              <div class="div-block-13"></div>
              <div class="text-block-6">oder</div>
              <div class="div-block-13"></div>
            </div>
            <div class="loginsubheader">Anmelden mit</div>
            <div class="loginbuttondiv">
              <Google
                response={(res) => {
                  console.log("Google response", res);
                  this.props.store.login(res, this.props.loggedIn);
                }}
              />
              <Apple />
            </div>
            <div class="loginbottomtext">
              Du hast noch kein Konto?Â 
              <a
                href="#"
                onClick={this.switchToRegister.bind(this)}
                class="link-5"
              >
                Registrieren
              </a>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
